package assignement1;

public class Divisibleby5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a = 1000;
    	
    	if (a%5 == 0){
    		System.out.println(a + " is divisible by 5.");
    	}else{
    		System.out.println(a + " is not divisible by 5.");
    	}
	}

}
